// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title EthereumHistory
 * @notice A gas-efficient contract for publishing and retrieving contract history data on Ethereum
 * @dev Stores content hashes in contract storage and full content in event logs for efficiency
 */
contract EthereumHistory {
    /// @notice Maps contract address to its current history content hash
    mapping(address => bytes32) public historyHashes;

    /// @notice Emitted when history is published or updated for a contract
    /// @param contractAddress The address of the contract whose history is being published
    /// @param contentHash The keccak256 hash of the serialized history content
    /// @param timestamp The block timestamp when the history was published
    /// @param contentJson The full serialized JSON content of the history
    event HistoryPublished(
        address indexed contractAddress,
        bytes32 indexed contentHash,
        uint256 timestamp,
        string contentJson
    );

    /// @notice Publishes or updates history for a contract
    /// @dev Anyone can publish history. The content hash is stored in contract storage,
    ///      and the full content is emitted in an event for on-chain archival.
    /// @param contractAddress The address of the contract whose history is being published
    /// @param contentHash The keccak256 hash of the serialized history content
    /// @param contentJson The full serialized JSON content of the history
    function publishHistory(
        address contractAddress,
        bytes32 contentHash,
        string calldata contentJson
    ) external {
        require(contractAddress != address(0), "EthereumHistory: invalid contract address");
        require(contentHash != bytes32(0), "EthereumHistory: invalid content hash");

        // Update the hash in storage (cheap for updates, more expensive for first write)
        historyHashes[contractAddress] = contentHash;

        // Emit full content in event (cheaper than storage, ~8 gas per byte)
        emit HistoryPublished(
            contractAddress,
            contentHash,
            block.timestamp,
            contentJson
        );
    }

    /// @notice Returns the current content hash for a contract
    /// @param contractAddress The address of the contract to query
    /// @return The content hash, or bytes32(0) if no history has been published
    function getHistoryHash(address contractAddress) external view returns (bytes32) {
        return historyHashes[contractAddress];
    }

    /// @notice Checks if history exists for a contract
    /// @param contractAddress The address of the contract to query
    /// @return True if history has been published, false otherwise
    function hasHistory(address contractAddress) external view returns (bool) {
        return historyHashes[contractAddress] != bytes32(0);
    }
}

